import "./Concepts.css";

const Concepts = (props) => {
  const title = props.concept.title;
  const image = props.concept.image;
  const description = props.concept.description;

  return (
    <div>
      <ul id="concepts">
        <li className="concept">
          <img src={image} alt={title} />
          <h2>{title}</h2>
          <p>{description}</p>
        </li>
      </ul>
    </div>
  );
};

export default Concepts;
